import React, { useState, useEffect } from 'react';

function Audios() {
  const [audioFiles, setAudioFiles] = useState([]);

  useEffect(() => {
    fetchAudioFiles();
  }, []);

  const fetchAudioFiles = async () => {
    try {
      const response = await fetch('http://localhost:5000/audify');
      if (response.ok) {
        const data = await response.json();
        setAudioFiles(data.audios[0].audioArray);
      } else {
        console.error('Failed to fetch audio files:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to fetch audio files:', error);
    }
  };

  return (
    <div>
      <h1>Audio Files</h1>
      {audioFiles.map((audio) => (
        <div key={audio._id}>
          <h2>{audio.filename}</h2>
          <audio controls>
            <source src={`http://localhost:5000/audify/${audio._id}`} type="audio/mpeg" />
          </audio>
        </div>
      ))}
    </div>
  );
}

export default Audios;
