import React, { useState, useEffect } from 'react';
import audio from "./output.mp3";
function Audios() {
  const [audioFiles, setAudioFiles] = useState([]);

  useEffect(() => {
    fetchAudioFiles();
  }, []);
  const email=JSON.parse(localStorage.getItem('useraudify')).email;
  const fetchAudioFiles = async () => {
    try {
      const response = await fetch(`http://localhost:5000/audify?email=${email}`);
      if (response.ok) {
        const data = await response.json();
        console.log(data.array);
        setAudioFiles(data.array);
      } else {
        console.error('Failed to fetch audio files:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to fetch audio files:', error);
    }
  };

  return (
    <>
    <div>
      <h1>Audio Files</h1>
      {/* {audioFiles.map((audio) => (
        <div key={audio._id}>
          <h2>{audio.filename}</h2>
          <audio controls>
          <source src={`../server/${audio.filename}`} type="audio/mp3" />
          </audio>
        </div>
      ))} */}
      <audio controls>
          <source src="./output.mp3" type="audio/mpeg" />
          </audio>
    </div>
    </>
  );
}

export default Audios;
