import React, { useState } from 'react';

function Audify() {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('video', selectedFile);
      const email=JSON.parse(localStorage.getItem('useraudify')).email;
    try {
      const response = await fetch(`http://localhost:5000/audify?email=${email}`, {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const blob = await response.blob();
        const downloadUrl = URL.createObjectURL(blob);

        // Create a link element and click it to initiate the file download
        const link = document.createElement('a');
        link.href = downloadUrl;
        link.download = 'output.mp3';
        link.click();
      } else {
        console.error('Conversion failed:', response.statusText);
      }
    } catch (error) {
      console.error('Conversion failed:', error);
    }
  };

  return (
    <div className="App" style={{"padding":"1%", "display":"flex"}}>
      <h1>Audify</h1>
     
      <p style={{"fontSize":"30px","margin":"1%","borderRadius":"10px"}}>Title</p>
      <input style={{"padding":"5px","fontSize":"20px","marginLeft":"1%","borderRadius":"10px","width":"30%"}} type="text"/>
      <p style={{"fontSize":"30px","margin":"1%","borderRadius":"10px"}}>Author</p>
      <input style={{"padding":"5px","fontSize":"20px","marginLeft":"1%","borderRadius":"10px","width":"30%"}} type="text"/>
      <p style={{"fontSize":"30px","margin":"1%","borderRadius":"10px"}}>Comment</p>
      <input style={{"padding":"5px","fontSize":"20px","marginLeft":"1%","borderRadius":"10px","width":"30%"}} type="text"/>
      <input type="file" accept="video/*" onChange={handleFileChange} />
      <button onClick={handleUpload} disabled={!selectedFile}>
        Convert
      </button>
    </div>
  );
}

export default Audify;
